import * as BABYLON from "babylonjs";
import Config from "./Config";
import dynamic from "next/dynamic";
// import "babylonjs-materials";
// import "../../public/js/vendor/babylon.gridMaterial.min.js";

const GridMaterial = dynamic(() => import("../../public/js/vendor/babylon.gridMaterial.min.js"), { ssr: false });


class Ground {
    constructor(scene) {
        this.scene = scene;

        this.defaultGround = new BABYLON.MeshBuilder.CreateGround("ground", {
            width: Config.ground.width,
            height: Config.ground.height
        }, this.scene);

        this.setMaterial();
    }

    setMaterial() {
        this.defaultGround.material = new BABYLON.GridMaterial("gridMaterial", this.scene);
    }
}

export default Ground;
