import * as BABYLON from "babylonjs";
import Config from "./Config";
import Utils from "./Utils";

class Camera {
    constructor(canvas, scene) {
        this.default = new BABYLON.ArcRotateCamera("Camera", Math.PI / 2, Math.PI / 2, 2, new BABYLON.Vector3(0,0,5), scene);
        this.default.maxZ = Config.camera.maxZ;

        if (Config.debug) {
            this.default.attachControl(canvas, true);
            Utils.showWorldAxis(1, scene);
        }
    }
}

export default Camera;
