export default {
    debug: {
        axis: true,
        axisSize: 1.5,
        cameraControls: true
    },
    canvas: {
        style: {
            background: "linear-gradient(180deg, #392357, #D781AE)"
        }
    },
    camera: {
        maxZ: 10,
        initialPosition: new BABYLON.Vector3(-1.5, 1, -1.5),
        initialTarget: new BABYLON.Vector3(0.5, 0.5, 0.5)
    },
    ground: {
        width: 10,
        height: 5,
        rotation: 0.785,
        grid: {
            mainColor: new BABYLON.Color4(1, 1, 1, 0),
            lineColor: new BABYLON.Color3(0, 1.0, 1.0),
            gridRatio: 0.1,
            majorUnitFrequency: 1,
            opacity: 0.98
        }
    }
}
