import * as BABYLON from "babylonjs";
import Config from "./Config";
import * as Materials from "babylonjs-materials";

class Ground {
    constructor(scene) {
        this.scene = scene;

        this.defaultGround = new BABYLON.Mesh.CreateGroundFromHeightMap(
            "ground", 
            Config.ground.heightMap, 
            Config.ground.width, 
            Config.ground.height, 
            Config.ground.subdivisions, 
            0, 
            Config.ground.textureHeight, 
            this.scene, 
            true
        );

        this.defaultGround.rotate(BABYLON.Axis.Y, Config.ground.rotation, BABYLON.Space.WORLD);

        this.setMaterial();
    }

    setMaterial() {
        this.grid = new Materials.GridMaterial("gridMaterial", this.scene);
        this.grid.mainColor = Config.ground.grid.mainColor;
        this.grid.lineColor = Config.ground.grid.lineColor;
        this.grid.gridRatio = Config.ground.grid.gridRatio;
        this.grid.backFaceCulling = false;
        this.grid.majorUnitFrequency = Config.ground.grid.majorUnitFrequency;
        this.grid.opacity = Config.ground.grid.opacity;
        this.defaultGround.material = this.grid;
    }
}

export default Ground;
