import Config from "./Config";

class Mountains {
    constructor(scene) {
        this.scene = scene;
        this.createMountains();
        this.setMaterial();
    }

    createMountains() {
        Config.mountains.forEach(mountain => {
            mountain.mesh = new BABYLON.Mesh.CreateGroundFromHeightMap(
                "ground", 
                Config.mountains.heightMap, 
                Config.mountains.width, 
                Config.mountains.height, 
                Config.mountains.subdivisions, 
                0, 
                Config.mountains.textureHeight, 
                this.scene, 
                true
            );

            mountain.mesh.rotate(BABYLON.Axis.Y, Config.mountains.rotation, BABYLON.Space.WORLD);
            mountain.mesh.position.x = Config.mountains.x;
            mountain.mesh.position.z = Config.mountains.z;
            mountain.mesh.position.y = Config.mountains.y;
        });
    }

    setMaterial() {
        Config.mountains.forEach(mountain => {
            let gridMaterial = new BABYLON.GridMaterial("gridMaterial", this.scene);

            gridMaterial.mainColor = Config.mountains.grid.mainColor;
            gridMaterial.lineColor = Config.mountains.grid.lineColor;
            gridMaterial.gridRatio = Config.mountains.grid.gridRatio;
            gridMaterial.backFaceCulling = false;
            gridMaterial.majorUnitFrequency = Config.mountains.grid.majorUnitFrequency;
            gridMaterial.opacity = Config.mountains.grid.opacity;
            gridMaterial.emissiveColor = Config.mountains.grid.lineColor;
            mountain.material = gridMaterial;
        });
    }
}

export default Mountains;
