import $ from "jquery";
import jQuery from "jquery";
import React from "react";
import Config from "./Config";
import "../../assets/scss/stars.scss";
import * as BABYLON from "babylonjs";

class Stars extends React.Component {
    constructor(props) {
        super(props);
        this.amount = 1;
        this.stars = [];
    }

    onClick(e) {
        e.preventDefault();
    }

    createStars() {
        // for (let i = 0; i < this.amount; i++) {
        //     let star = BABYLON.MeshBuilder.CreateSphere("sphere", {
        //         diameter : 2
        //     }, this.scene);

        //     // this.stars.push(star);
        //     console.log("yo");
        // }
        let star = BABYLON.MeshBuilder.CreateSphere("sphere", {
            diameter : 2
        }, this.scene);
    }

    componentDidMount() {
        this.scene = this.props.world.scene;
        this.createStars();
        // window.jQuery = jQuery;

        // require("../../public/js/vendor/flexBackground.js");

        // $("#stars").flexBackground({
        //     numberOfPoints: Config.stars.numberOfPoints,
		// 	radius: Config.stars.radius,
		// 	interval: Config.stars.interval,
		// 	color: Config.stars.color
        // });
    }

    render() {
        return (
            <canvas onClick={ this.onClick } style={ Config.stars.styles } id="stars"></canvas>
        );
    }
}

export default Stars;
