import React from "react";
import * as BABYLON from 'babylonjs';
import Config from "../../world.config.js";

class World extends React.Component {
    constructor(props) {
        super(props);
        this.canvas = React.createRef();
    }

    resize() {
        console.log(this);
        this.canvas.current.width = window.innerWidth;
        this.canvas.current.height = window.innerHeight;
        this.engine.resize();
    }

    componentDidMount() {
        this.engine = new BABYLON.Engine(this.canvas.current, true);
        this.scene = new BABYLON.Scene(this.engine);
        this.camera = new BABYLON.ArcRotateCamera("Camera", Math.PI / 2, Math.PI / 2, 2, new BABYLON.Vector3(0,0,5), this.scene);
        this.light1 = new BABYLON.HemisphericLight("light1", new BABYLON.Vector3(1, 1, 0), this.scene);
        this.sphere = BABYLON.MeshBuilder.CreateSphere("sphere", {diameter:2}, this.scene);

        if (Config.debug) {
            this.camera.attachControl(this.canvas.current, true);
        }

        this.engine.runRenderLoop(() => { 
            this.scene.render();
        });

        this.resize();

        window.addEventListener("resize", () => { 
            this.resize();
        });
    }

    render() {
        // this.camera.attachControl(canvas, true);

        // this.engine.runRenderLoop(() => { 
        //     console.log(this);
        //     this.scene.render();
        // });

        // window.addEventListener("resize", () => { 
        //     this.engine.resize();
        // });

        return (
            <canvas 
                id="canvas"
                ref={ this.canvas }>
            </canvas>
        );
    }
}

export default World;
