class DynamicGround {
    constructor(scene) {
        this.scene = scene;

        let mapSubX = 500,
            mapSubZ = 500,
            mapData = new Float32Array(mapSubX * mapSubZ * 3);   

        // for (var l = 0; l < mapSubZ; l++) {           
        //     for (var w = 0; w < mapSubX; w++) {                
        //         mapData[3 *(l * mapSubX + w)] = (w - mapSubX * 0.5) * 2.0;
        //         mapData[3 * (l * mapSubX + w) + 1] = w / (l +1) * Math.sin(l / 2) * Math.cos(w / 2) * 2.0;
        //         mapData[3 * (l * mapSubX + w) + 2] = (l - mapSubZ * 0.5) * 2.0;
        //    }            
        // }

        var params = {
            mapData: mapData,
            mapSubX: mapSubX,
            mapSubZ: mapSubZ,
            terrainSub: 200
        };

        this.ground = new BABYLON.DynamicTerrain("terrain", params, this.scene);
        this.ground.subToleranceX = 16;
        this.ground.subToleranceZ = 16;
        this.ground.LODLimits = [4, 3, 2, 1, 1];
        this.terrainCreated = true;
    }

    setMaterial() {
        this.gridMaterial = new BABYLON.GridMaterial("gridMaterial", this.scene);
        this.gridMaterial.mainColor = Config.ground.static.grid.mainColor;
        this.gridMaterial.lineColor = Config.ground.static.grid.lineColor;
        this.gridMaterial.gridRatio = Config.ground.static.grid.gridRatio;
        this.gridMaterial.backFaceCulling = false;
        this.gridMaterial.majorUnitFrequency = Config.ground.static.grid.majorUnitFrequency;
        this.gridMaterial.opacity = Config.ground.static.grid.opacity;
        this.ground.mesh.material = this.gridMaterial;
    }
}

export default DynamicGround;
