import Config from "./Config";

class Mountains {
    constructor(scene) {
        this.scene = scene;
        this.createGround();
        this.setMaterial();
    }

    createGround() {
        this.defaultMountains = new BABYLON.Mesh.CreateGroundFromHeightMap(
            "ground", 
            Config.mountains.heightMap, 
            Config.mountains.width, 
            Config.mountains.height, 
            Config.mountains.subdivisions, 
            0, 
            Config.mountains.textureHeight, 
            this.scene, 
            true
        );
        this.defaultMountains.rotate(BABYLON.Axis.Y, Config.mountains.rotation, BABYLON.Space.WORLD);
        this.defaultMountains.position.x = Config.mountains.x;
        this.defaultMountains.position.z = Config.mountains.z;
    }

    setMaterial() {
        this.gridMaterial = new BABYLON.GridMaterial("gridMaterial", this.scene);
        this.gridMaterial.mainColor = Config.mountains.grid.mainColor;
        this.gridMaterial.lineColor = Config.mountains.grid.lineColor;
        this.gridMaterial.gridRatio = Config.mountains.grid.gridRatio;
        this.gridMaterial.backFaceCulling = false;
        this.gridMaterial.majorUnitFrequency = Config.mountains.grid.majorUnitFrequency;
        this.gridMaterial.opacity = Config.mountains.grid.opacity;
        this.gridMaterial.emissiveColor = Config.mountains.grid.lineColor;
        this.defaultMountains.material = this.gridMaterial;
    }
}

export default Mountains;
