import React from "react";
import "../../public/js/vendor/granim.min.js";
import "../../assets/scss/backgrounds.scss";

class Backgrounds extends React.Component {
    constructor(props) {
        super(props);
    }

    componentDidMount() {
        this.colorBackground = new Granim({
            element: '#color-background',
            direction: 'top-bottom',
            isPausedWhenNotInView: true,
            states : {
                "default-state": {
                    gradients: [
                        ['#ff9966', 'rgba(0, 0, 0, 0)'],
                        ['#00F260', 'rgba(0, 0, 0, 0)'],
                        ['#e1eec3', 'rgba(0, 0, 0, 0)']
                    ]
                }
            }
        });
    }

    render() {
        return (
            <div id="backgrounds">
                <canvas id="color-background"></canvas>
            </div>
        );
    }
}

export default Backgrounds;
