import React from "react";
import Config from "./Config";
import "../../public/js/vendor/granim.min.js";
import "../../assets/scss/backgrounds.scss";

class Backgrounds extends React.Component {
    constructor(props) {
        super(props);
    }

    componentDidMount() {
        this.colorBackground = new Granim({
            element: '#color-background',
            direction: 'top-bottom',
            isPausedWhenNotInView: true,
            states : {
                "default-state": {
                    gradients: [
                        ['rgba(0, 0, 0, 0)', 'rgba(38, 2, 33, 0.75)'],
                ['rgba(0, 0, 0, 0)', 'rgba(101, 13, 137, 0.75)'],
                ['rgba(0, 0, 0, 0)', 'rgba(255, 108, 17, 0.75)'],
                ['rgba(0, 0, 0, 0)', 'rgba(246, 1, 157, 0.75)'],
                ['rgba(0, 0, 0, 0)', 'rgba(121, 30, 148, 0.75)']
                    ]
                }
            }
        });
    }

    render() {
        return (
            <div id="backgrounds">
                <canvas id="color-background"></canvas>
            </div>
        );
    }
}

export default Backgrounds;
