export default {
    debug: {
        axis: true,
        axisSize: 1.5,
        cameraControls: true
    },
    canvas: {
        style: {
            background: "linear-gradient(180deg, #392357, #D781AE)"
        }
    },
    camera: {
        type: "free",
        maxZ: 10,
        initialPosition: new BABYLON.Vector3(-1.5, 1, -1.5),
        initialTarget: new BABYLON.Vector3(0.5, 0.5, 0.5),
        walk: true,
        walkSpeed: 0.01
    },
    effects: {
        glow: {
            intensity: 5,
            mainTextureSamples: 4
        }
    },
    ground: {
        heightMap: "images/height_map4.png",
        width: 5,
        height: 7,
        textureHeight: 0.5,
        subdivisions: 15,
        rotation: 0.785,
        grid: {
            mainColor: new BABYLON.Color4(1, 1, 1, 0.5),
            lineColor: new BABYLON.Color3.FromHexString("#e236be"),
            gridRatio: 0.1,
            majorUnitFrequency: 1,
            opacity: 0.9
        }
    }
}
