import * as BABYLON from "babylonjs";
import Config from "./Config";
import Utils from "./Utils";

class Camera {
    constructor(canvas, scene) {
        this.defaultCamera = new BABYLON.ArcRotateCamera("Camera", 0, 0, 0, new BABYLON.Vector3(0, 0, 10), this.scene);
        this.defaultCamera.setTarget(BABYLON.Vector3.Zero());
        this.defaultCamera.setPosition(Config.camera.initialPosition);
        // this.default.maxZ = Config.camera.maxZ;

        if (Config.debug) {
            this.defaultCamera.attachControl(canvas, true);
            Utils.showWorldAxis(1, scene);
        }
    }
}

export default Camera;
