import Config from "./Config";
class Controls {
    constructor(world) {
        this.world = world;
    }

    update() {
        // Camera
        this.world.camera.position.x += Config.camera.walkSpeed;
        this.world.camera.position.z += Config.camera.walkSpeed;
    }
}

export default Controls;
