    backgrounds: {
        style: {
            zIndex: "3"
        },
        centerGradientBackground: {
            style: {
                zIndex: "3",
                background: "radial-gradient(circle, rgba(50,37,107,1) 0%, rgba(14,8,32,1) 100%)"
            }
        },
        bottomColorBackground: {
            gradients: [
                ['rgba(0, 0, 0, 0)', 'rgb(45, 226, 230)'],
                ['rgba(0, 0, 0, 0)', 'rgb(101, 13, 137)'],
                ['rgba(0, 0, 0, 0)', 'rgb(255, 108, 17)'],
