import * as BABYLON from "babylonjs";
import Config from "./Config";
import Utils from "./Utils";

class Camera {
    constructor(canvas, scene) {
        // this.defaultCamera = new BABYLON.ArcRotateCamera("Camera", 0, 0, 0, Config.camera.initialPosition, this.scene);
        this.defaultCamera = new BABYLON.UniversalCamera("UniversalCamera", Config.camera.initialPosition, this.scene);
        this.defaultCamera.setTarget(Config.camera.initialTarget);
        // this.default.maxZ = Config.camera.maxZ;

        if (Config.debug.cameraControls) {
            this.defaultCamera.attachControl(canvas, true);
        }
    }
}

export default Camera;
